    private void ListaVaciaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ListaVaciaActionPerformed
        lista = new Lista();
    }//GEN-LAST:event_ListaVaciaActionPerformed

    private void AgregarNodoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AgregarNodoActionPerformed
        Nodo nodo = new Nodo(Nombre.getText(), Long.parseLong(Codigo.getText()));
        if(lista.getCabeza() == null){
            lista.setCabeza(nodo);
        }else{
            lista.insertarNodo(nodo, lista.getCabeza());
        }
    }//GEN-LAST:event_AgregarNodoActionPerformed

    private void MostrarListaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_MostrarListaActionPerformed
        lista.setLista("");
        lista.mostrarLista(lista.getCabeza());
        Resultado.setText(lista.getLista());
    }//GEN-LAST:event_MostrarListaActionPerformed

    private void BuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BuscarActionPerformed
        lista.buscarNodo(lista.getCabeza(), Long.parseLong(CodigoB.getText()));
    }//GEN-LAST:event_BuscarActionPerformed

